import * as actionTypes from '../actions/actionTypes';

export const addItem = (itemID, size, amount) => {
    return {
        type: actionTypes.ADD_ITEM,
        itemID: itemID,
        size: size,
        amount: amount
    };
};

export const pullShopItems = () =>{
    return {
        type: actionTypes.PULL_SHOP_ITEMS
    }
}
